<div class="contact_form_container">
    <div class="container row">
        <div class="contact_info col-2 col-sm">
            <h2>Contacts</h2>
            <div class="details row">
                <div class="icon">
                    <img src="../dist/img/icons/location-pin.png" alt="">
                </div>
                <div class="text">
                    <p>111111, Thailand, Phuket Chalong 1-45902-20</p>
                </div>
            </div>
            <div class="details row">
                <div class="icon">
                    <img src="../dist/img/icons/phone-call-contact.png" alt="">
                </div>
                <div class="text">
                    <p>+66032844782</p>
                </div>
            </div>
            <div class="details row">
                <div class="icon">
                    <img src="../dist/img/icons/email-contact.png" alt="">
                </div>
                <div class="text">
                    <p>info@website.com</p>
                </div>
            </div>
        </div>
        <div class="contact_form col-2 col-sm">
            <div class="form_container">
                <input type="text" name="" id="" placeholder="Your Name">
                <input type="text" name="" id="" placeholder="Your Phone">
                <div class="btn_group row">
                    <div class="col-30 col-sm button">
                        <input type="submit" value="Call Me" class="btn secondary">
                    </div>
                    <div class="consent_group row col-70 col-sm">
                        <input type="checkbox" name="consent" id="" class="col-10">
                        <option name="consent" value="" class="col-70">
                            I agree with processing of my personal data due to <a href="#">Privacy Policy</a>

                        </option>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>